mapper.select:
select B.USER_ID, B.GROUP_ID, B.THEME_ID, P.* FROM BOOKMARKS B inner join PLACES P on B.PLACE_ID = P.PLACE_ID WHERE B.GROUP_ID = #{groupId} and B.THEME_ID IN
#{item}
ORDER BY B.THEME_ID

mapper.delete:
delete
from BOOKMARKS
where GROUP_ID = #{groupId}
  and PLACE_ID = #{placeId}
  and THEME_ID = #{themeId}
