mapper.select:
select count(*)
from `GROUPS`
where GROUP_ID = #{groupId}
  and USER_ID = #{userId}

mapper.update:
update `GROUPS`
set USER_ID = #{userId},
    NAME    = #{name}
where GROUP_ID = #{groupId}

mapper.delete:
delete
from GROUP_MEMBERS
where GROUP_ID = #{groupId}
  and USER_ID = #{userId}
