mapper.delete:
delete
from THEMES
where GROUP_ID = #{groupId}
  and THEME_ID = #{themeId}

mapper.select:
select G.GROUP_ID, GROUP_CONCAT(T.THEME_ID SEPARATOR ',') as "themes"
from (
    select GROUP_ID
    FROM GROUP_MEMBERS
    WHERE USER_ID = #{userId}
    ) G
inner join THEMES T WHERE T.GROUP_ID = G.GROUP_ID
GROUP BY G.GROUP_ID
